import React, { useState } from "react";
import { NavLink, Link, useNavigate } from "react-router-dom";
import { getAuth, signOut } from "firebase/auth";
const NavBar = ({ firebaseUser }) => {
  const navigate = useNavigate();
  //cerrar sesion activa
  const auth = getAuth();
  const logOut = () => {
    signOut(auth)
      .then(() => {
        navigate("../", { replace: true });
      })
      .catch((error) => {
        console.log(error);
      });
  };
  return (
    <div>
      <nav className="flex flex-col lg:flex-row items-center justify-between flex-wrap h-30 bg-teal-500 p-6">
        <div></div>
        <div className="  text-white mr-6">
          <Link to="/PanelControl">
            <img
              src="https://i.imgur.com/iMYhMud.png"
              alt="imagen Usuario"
              className="w-40 max-auto ml-5"
            />
          </Link>
        </div>
        <div className=" w-full  flex-grow lg:flex lg:items-center lg:w-auto">
          <div className="text-sm lg:flex-grow">
            {firebaseUser !== null && (
              <div>
                <Link
                  to="/PanelControl"
                  className="rounded-sm bg-teal-500 hover:bg-teal-600 active:bg-teal-700 focus:outline-none focus:ring focus:ring-teal-300 ... mr-5"
                >
                  PANEL DE CONTROL
                </Link>
                <Link
                  to="/productos"
                  className="rounded-sm bg-teal-500 hover:bg-teal-600 active:bg-teal-700 focus:outline-none focus:ring focus:ring-teal-300 ... mr-5"
                >
                  PRODUCTOS
                </Link>
                <Link
                  to="/clientes"
                  className=" rounded-sm bg-teal-500 hover:bg-teal-600 active:bg-teal-700 focus:outline-none focus:ring focus:ring-teal-300 ... mr-5"
                >
                  CLIENTES
                </Link>
                <Link
                  to="/facturaform"
                  className=" rounded-sm bg-teal-500 hover:bg-teal-600 active:bg-teal-700 focus:outline-none focus:ring focus:ring-teal-300 ... mr-5"
                >
                  PROFORMA(COTIZACIONES)
                </Link>
                <button
                  className="rounded-sm font-bold bg-teal-500 hover:bg-teal-600 active:bg-teal-700 focus:outline-none focus:ring focus:ring-teal-300 ... mr-5 text-redO"
                  onClick={logOut}
                >
                  Â¡CERRAR SESION!
                </button>
                <div className="float-none mr-2 md text-center md:float-right ">
                  <h1 className="uppercase mr-2 border p-4 flex items-center">
                    BIENVENIDO: {auth.currentUser.email}
                    <img
                      src="https://upload.wikimedia.org/wikipedia/commons/9/99/Sample_User_Icon.png"
                      alt="imagen Usuario"
                      className="w-10 max-auto float-right ml-5"
                    />
                  </h1>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
